(function() {
  var bilby, boolean, either, should;

  bilby = require('bilby');

  boolean = require('./boolean');

  either = require('./either');

  should = require('should');

  describe('either', function() {
    return describe('eitherize()', function() {
      var f;
      f = either.eitherize(boolean.truthy);
      it('should return left if null', function() {
        return f(null).isLeft.should.be["true"];
      });
      it('should return right if 0', function() {
        return f(0).isRight.should.be["true"];
      });
      it('should return right if an empty string', function() {
        return f('').isRight.should.be["true"];
      });
      it('should return right if an empty array', function() {
        return f([]).isRight.should.be["true"];
      });
      it('should return right if an empty object', function() {
        return f({}).isRight.should.be["true"];
      });
      it('should return right if true', function() {
        return f(true).isRight.should.be["true"];
      });
      return it('should return left if false', function() {
        return f(false).isLeft.should.be["true"];
      });
    });
  });

}).call(this);
