(function() {
  var boolean, either, k, make, v;

  boolean = require('./boolean');

  make = require('./make');

  either = {
    eitherize: function(f) {
      return make.template(f, make.either);
    }
  };

  for (k in boolean) {
    v = boolean[k];
    either[k] = either.eitherize(v);
  }

  module.exports = Object.freeze(either);

}).call(this);
