(function() {
  var bilby, boolean, make;

  bilby = require('bilby');

  boolean = require('./boolean');

  make = {
    either: function(a, b) {
      if (boolean.truthy(a)) {
        return bilby.right(b);
      } else {
        return bilby.left(b);
      }
    },
    option: function(a, b) {
      if (boolean.truthy(a)) {
        return bilby.some(b);
      } else {
        return bilby.none;
      }
    },
    template: function(bfn, mfn) {
      return function(a, b) {
        return mfn(bilby.bind(bfn)(boolean, a)(), a, b);
      };
    },
    validation: function(a, b, c) {
      if (boolean.truthy(a)) {
        return bilby.success(b);
      } else {
        return bilby.failure(c);
      }
    }
  };

  module.exports = Object.freeze(make);

}).call(this);
