(function() {
  var bilby, boolean, option, should;

  bilby = require('bilby');

  boolean = require('./boolean');

  option = require('./option');

  should = require('should');

  describe('option', function() {
    return describe('optionize()', function() {
      var f;
      f = option.optionize(boolean.truthy);
      it('should return none if null', function() {
        return f(null).isNone.should.be["true"];
      });
      it('should return some if 0', function() {
        return f(0).isSome.should.be["true"];
      });
      it('should return some if an empty string', function() {
        return f('').isSome.should.be["true"];
      });
      it('should return some if an empty array', function() {
        return f([]).isSome.should.be["true"];
      });
      it('should return some if an empty object', function() {
        return f({}).isSome.should.be["true"];
      });
      it('should return some if true', function() {
        return f(true).isSome.should.be["true"];
      });
      return it('should return none if false', function() {
        return f(false).isNone.should.be["true"];
      });
    });
  });

}).call(this);
