(function() {
  var boolean, k, make, option, v;

  boolean = require('./boolean');

  make = require('./make');

  option = {
    optionize: function(f) {
      return make.template(f, make.option);
    }
  };

  for (k in boolean) {
    v = boolean[k];
    option[k] = option.optionize(v);
  }

  module.exports = Object.freeze(option);

}).call(this);
