(function() {
  var boolean, k, make, v, validation;

  boolean = require('./boolean');

  make = require('./make');

  validation = {
    validationize: function(f) {
      return make.template(f, make.validation);
    }
  };

  for (k in boolean) {
    v = boolean[k];
    validation[k] = validation.validationize(v);
  }

  module.exports = Object.freeze(validation);

}).call(this);
