(function() {
  var encode, should;

  should = require('should');

  encode = require('./encode');

  describe('encode', function() {
    describe('rfc3986()', function() {
      return it('should return RFC 3986 compatible string', function() {
        return encode.rfc3986('hello there!').should.equal('hello%20there%21');
      });
    });
    return describe('url()', function() {
      return it('should return string representation of a map', function() {
        return encode.url({
          test2: 'test2',
          test1: 'test1'
        }).should.equal('test1=test1&test2=test2');
      });
    });
  });

}).call(this);
