(function() {
  var Http, http, should;

  Http = require('./http');

  should = require('should');

  http = new Http(process.env.LTIJS_URL, process.env.LTIJS_CONSUMERKEY, process.env.LTIJS_CONSUMERSECRET);

  describe('Http', function() {
    return describe('post()', function() {
      return it('should work', function(done) {
        var payload;
        payload = {
          lti_version: 'LTI-1p0',
          lti_message_type: 'basic-lti-launch-request',
          resource_link_id: '0'
        };
        return http.post(payload).then(function(response) {
          response.isSome.should.be["true"];
          return response.getOrElse('').indexOf('Context Information:').should.be.above(-1);
        }).done(function() {
          return done();
        });
      });
    });
  });

}).call(this);
